import React, { useState } from 'react';
import { Container, Form, Button, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
    setError('');
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
    setError('');
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validación
    if (!email || !password) {
      setError('Please enter email and password.');
      return;
    }

    // Aquí puedes realizar la lógica de autenticación o enviar los datos a un servidor
    console.log('Email:', email);
    console.log('Password:', password);

    // Limpiar campos y errores después del envío exitoso
    setEmail('');
    setPassword('');
    setError('');
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <div className="login-container">
        <Form onSubmit={handleSubmit}>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form.Group controlId="formEmail">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={handleEmailChange}
              className={error && !email ? 'is-invalid' : ''}
            />
            {error && !email && <Form.Control.Feedback type="invalid">Please enter your email.</Form.Control.Feedback>}
          </Form.Group>

          <Form.Group controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Password"
              value={password}
              onChange={handlePasswordChange}
              className={error && !password ? 'is-invalid' : ''}
            />
            {error && !password && <Form.Control.Feedback type="invalid">Please enter your password.</Form.Control.Feedback>}
          </Form.Group>

          <Button variant="primary" type="submit">Login</Button>
        </Form>
      </div>
    </Container>
  );
};

export default Login;
