import { Modal, Button, Form } from 'react-bootstrap';
import { useState } from 'react';


const SearchMovie = () => {
  const [showModal, setShowModal] = useState(false);
  const [movieName, setMovieName] = useState(''); //name at same time is filter
  const [filteredMovies, setFilteredMovies] = useState([]);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
        <button type="button" className="DocSearch DocSearch-Button MyButton" aria-label="Search" onClick={handleOpenModal}>
            <span className="DocSearch-Button-Container">
                <svg width="20" height="20" className="DocSearch-Search-Icon" viewBox="0 0 20 20">
                <path d="M14.386 14.386l4.0877 4.0877-4.0877-4.0877c-2.9418 2.9419-7.7115 2.9419-10.6533 0-2.9419-2.9418-2.9419-7.7115 0-10.6533 2.9418-2.9419 7.7115-2.9419 10.6533 0 2.9419 2.9418 2.9419 7.7115 0 10.6533z" stroke="currentColor" fill="none" fill-rule="evenodd" stroke-linecap="round" stroke-linejoin="round"></path>
                </svg>
                <span className="DocSearch-Button-Placeholder">Search</span>
            </span>
        </button>

        <Modal show={showModal} onHide={handleCloseModal} size='lg'>
            <Modal.Body>
                <div className='row mb-4 px-2'>
                    <Form.Control placeholder="Write the movie's name" className='rounded-4' tabindex="-1" autoFocus />
                </div>
                <div className='row text-center'>
                    <div className='col'>
                        <ul className="list-group">
                            <li className="list-group-item">Frase 1</li>
                            <li className="list-group-item">Frase 2</li>
                        </ul>
                    </div>
                </div>            
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={handleCloseModal}>
                    Cerrar
                </Button>
            </Modal.Footer>
        </Modal>

    </div>
  );
};

export default SearchMovie;
