import CustomNav from "../customNav/CustomNav";
import "./Menu.css";
import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';


function Menu() {

  const [filmData, setFilmData] = useState([]);

  useEffect(() => {
    fetch('https://gist.githubusercontent.com/saniyusuf/406b843afdfb9c6a86e25753fe2761f4/raw/523c324c7fcc36efab8224f9ebb7556c09b69a14/Film.JSON')
      .then(response => response.json())
      .then(data => setFilmData(data))
      .catch(error => console.log(error));
  }, []);


  return (
    <div>

      <CustomNav />

      <div className="container">
        <div className="row">
        {filmData.map(film => (
            <div className="col col-xs-12 col-sm-6 col-md-4 mt-4" key={film.imdbID} style={{opacity: 0.8, cursor: 'pointer'}}>
              <Card style={{height: '100%'}}>
                <Card.Img variant="top" src={film.Images[0]} style={{height: '100%'}}/>
                <Card.Body>
                  <Card.Title>
                    {film.Title}
                  </Card.Title>
                  <Card.Text>
                    {film.Runtime}
                  </Card.Text>
                  <a href="#" className="btn btn-primary">Ver detalles</a>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>


    </div>
  );
}

export default Menu;
