import { Modal, Button } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import MovieRating from '../movieRating/MovieRating';


const MovieDetails = ({ movieDetailSelected, show, close }) => {
  const [showModal, setShowModal] = useState(show);

  useEffect(() => {
    setShowModal(show);
    console.log(movieDetailSelected);
  }, [show]);

  const handleCloseModal = () => close();

  return (
    <div>
      <Modal show={showModal} size='lg' onHide={handleCloseModal}>
        <Modal.Header closeButton className='bg-secondary'>
          <Modal.Title style={{marginRight: '5px'}}> {movieDetailSelected?.Title} </Modal.Title>
          <small><MovieRating rating={movieDetailSelected?.imdbRating}/></small>
        </Modal.Header>
        <Modal.Body style={{maxHeight: '60vh', overflowY: 'auto'}}>
          <p>Type: {movieDetailSelected?.Type}</p>
          <p>Plot: {movieDetailSelected?.Plot}</p>
          <p>Director: {movieDetailSelected?.Director}</p>
          <p>Actors: {movieDetailSelected?.Actors}</p>
          <p>Awards: {movieDetailSelected?.Awards}</p>
          <p>Genre: {movieDetailSelected?.Genre}</p>
          <p>Country: {movieDetailSelected?.Country}</p>
          <p>Language: {movieDetailSelected?.Language}</p>
          <p>Rated: {movieDetailSelected?.Rated}</p>
          <p>Released: {movieDetailSelected?.Released}</p>
          <p>Year: {movieDetailSelected?.Year}</p>
          <p>Runtime: {movieDetailSelected?.Runtime}</p>
          <p>Writer: {movieDetailSelected?.Writer}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cerrar
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default MovieDetails;
