import React, { useEffect, useState } from 'react';
import { Card, Carousel } from 'react-bootstrap';


function CustomCardImg({title='', year='', duration='', images=[]}) {

  const [filmData, setFilmData] = useState([]);

  useEffect(() => {
    fetch('https://gist.githubusercontent.com/saniyusuf/406b843afdfb9c6a86e25753fe2761f4/raw/523c324c7fcc36efab8224f9ebb7556c09b69a14/Film.JSON')
      .then(response => response.json())
      .then(data => setFilmData(data))
      .catch(error => console.log(error));
  }, []);


  return (
    <Carousel>
        {
            images.map((image, index) => (
                <Carousel.Item key={index}>
                    <Card.Img
                    className="d-block w-100"
                    src={image}
                    alt="Primer slide"
                    />
                    <Carousel.Caption>
                        <div style={{backgroundColor: 'rgba(0, 0, 0, 0.7)'}}>
                            <h4>{title} ({year})</h4>
                        </div>
                        <p>Descripci√≥n del primer slide.</p>
                    </Carousel.Caption>
                </Carousel.Item>
            ))
        }

    </Carousel>

  );
}

export default CustomCardImg;
