import React, { useState } from 'react';
import { Container, Form, Button, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css';
import { Puff } from 'react-loader-spinner';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [isLoading, setIsLoading] = useState(false);


  const handleEmailChange = (event) => {
    const value = event.target.value;
    setEmail(value);
    setEmailError(value ? '' : 'Please enter your email.');
  };

  const handlePasswordChange = (event) => {
    const value = event.target.value;
    setPassword(value);
    setPasswordError(value ? '' : 'Please enter your password.');
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validación
    if (!email || !password) {
      setEmailError(email ? '' : 'Please enter your email.');
      setPasswordError(password ? '' : 'Please enter your password.');
      return;
    }

    // Aquí puedes realizar la lógica de autenticación o enviar los datos a un servidor
    console.log('Email:', email);
    console.log('Password:', password);

    if ( email.toLowerCase().trim() == 'test@gmail.com' && password === '12345678' )
    {
        alert("correct");
    }
        
    else
    {
        alert("correct");
    }


    // Limpiar campos y errores después del envío exitoso
    setEmail('');
    setPassword('');
    setEmailError('');
    setPasswordError('');
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <div className="login-container">
        <Form onSubmit={handleSubmit}>
          {(emailError || passwordError) && <Alert variant="danger">{emailError || passwordError}</Alert>}
          <Form.Group controlId="formEmail">
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={handleEmailChange}
              className={emailError ? 'is-invalid' : ''}
            />
          </Form.Group>
          <div className='mt-3'></div>
          <Form.Group controlId="formPassword">
            <Form.Control
              type="password"
              placeholder="Password"
              value={password}
              onChange={handlePasswordChange}
              className={passwordError ? 'is-invalid' : ''}
            />
          </Form.Group>
          <div className='mt-3'></div>
          {isLoading ? <Puff color="#00BFFF" height={50} width={50} /> : <Button variant="primary" type="submit">Login</Button>}
        </Form>
      </div>
    </Container>
  );
};

export default Login;
