import React, { useEffect, useState } from 'react';
import { Card, Carousel } from 'react-bootstrap';


function CustomCardImg({images}) {

  const [filmData, setFilmData] = useState([]);

  useEffect(() => {
    fetch('https://gist.githubusercontent.com/saniyusuf/406b843afdfb9c6a86e25753fe2761f4/raw/523c324c7fcc36efab8224f9ebb7556c09b69a14/Film.JSON')
      .then(response => response.json())
      .then(data => setFilmData(data))
      .catch(error => console.log(error));
  }, []);


  return (
    <Carousel>
        {
            images.map((image, index) => (
                <div className="col col-xs-12 col-sm-6 col-md-4 mt-4" key={index} style={{opacity: 0.8, cursor: 'pointer'}}>
                <Card style={{height: '100%'}}>
                    {/*<Card.Img variant="top" src={film.Images[0]} style={{height: '100%'}}/> */}
                    <CustomCardImg />
                    <Card.Body>
                    <Card.Title className="text-primary" style={{textDecoration: 'underline'}}>
                        {film.Title} <small> ({film.Year})</small>
                    </Card.Title>
                    <Card.Text>
                        {film.Runtime}
                    </Card.Text>
                    <a href="#" className="btn btn-primary">Ver detalles</a>
                    </Card.Body>
                </Card>
                </div>
            ))
        }
        <Carousel.Item>
            <img
            className="d-block w-100"
            src="ruta-a-tu-imagen-1.jpg"
            alt="Primer slide"
            />
            <Carousel.Caption>
            <h3>Título del primer slide</h3>
            <p>Descripción del primer slide.</p>
            </Carousel.Caption>
        </Carousel.Item>
        {/* Agrega más items según sea necesario */}
    </Carousel>

  );
}

export default CustomCardImg;
