import CustomCardImg from "../customCardImg/CustomCardImg";
import CustomFooter from "../customFooter/CustomFooter";
import CustomNav from "../customNav/CustomNav";
import "./Menu.css";
import React, { useEffect, useState } from 'react';
import { Card, Carousel } from 'react-bootstrap';


function Menu() {

  const [filmData, setFilmData] = useState([]);

  useEffect(() => {
    fetch('https://gist.githubusercontent.com/saniyusuf/406b843afdfb9c6a86e25753fe2761f4/raw/523c324c7fcc36efab8224f9ebb7556c09b69a14/Film.JSON')
      .then(response => response.json())
      .then(data => setFilmData(data))
      .catch(error => console.log(error));
  }, []);


  return (
    <div>

      <div className="sticky-top">
        <CustomNav />
      </div>

      <div className="container mb-4">
        <div className="row">
        {filmData.map(film => (
            <div className="col col-xs-12 col-sm-6 col-md-4 mt-4" key={film.imdbID} style={{opacity: 0.8}}>
              <Card style={{height: '100%'}} className="bg-primary">
                <CustomCardImg title={film.Title} year={film.Year} duration={film.Runtime} images={film.Images}/>
                <Card.Body>
                  <Card.Title className="text-primary" style={{textDecoration: 'underline'}}>
                    {film.Title} <small> ({film.Year})</small>
                  </Card.Title>
                  <Card.Text>
                    {film.Plot}
                  </Card.Text>
                  <a href="#" className="btn btn-primary">More details</a>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </div>

      <CustomFooter/>

    </div>
  );
}

export default Menu;
